styles:
.form-group {
margin: 0;
max-width: 100%;
position: relative;
}

.selector-container {
max-width: 100%;
width: 100%;
position: relative;
}

.textarea-selector {
resize: none;
padding-right: 34px;
overflow-x: hidden;
}

.drop-menu {
background: linear-gradient(to bottom, white 0, #f2f2f2 100%);
border-color: #ccc;
border-radius: 3px;
border-style: solid;
border-width: 1px;
color: #333;
cursor: pointer;
display: inline-block;
font-size: 14px;
font-family: inherit;
font-variant: normal;
line-height: 20px;
margin: 0 10px 0 0;
padding: 4px 10px;
text-decoration: none;
text-shadow: 0 1px 0 white;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
-ms-box-sizing: border-box;
box-sizing: border-box;
-moz-border-radius-bottomleft: 0;
border-bottom-left-radius: 0;
-moz-border-radius-topleft: 0;
border-top-left-radius: 0;
border-top: 0;
border-right: 0;
border-bottom: 0;
display: block;
height: auto;
margin: 0;
padding: 4px 0;
position: absolute;
right: 1px;
top: 1px;
bottom: 1px;
width: 23px;
}

.drop-menu-disabled {
background: linear-gradient(to bottom, #e0dede 0, #c7c6c6 100%);
border-color: #ccc;
border-radius: 3px;
border-style: solid;
border-width: 1px;
color: #333;
cursor: pointer;
display: inline-block;
font-size: 14px;
font-family: inherit;
font-variant: normal;
line-height: 20px;
margin: 0 10px 0 0;
padding: 4px 10px;
text-decoration: none;
text-shadow: 0 1px 0 white;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
-ms-box-sizing: border-box;
box-sizing: border-box;
-moz-border-radius-bottomleft: 0;
border-bottom-left-radius: 0;
-moz-border-radius-topleft: 0;
border-top-left-radius: 0;
border-top: 0;
border-right: 0;
border-bottom: 0;
display: block;
height: auto;
margin: 0;
padding: 4px 0;
position: absolute;
right: 1px;
top: 1px;
bottom: 1px;
width: 23px;
}

.drop-menu-disabled .fa-spinner {
margin: 4px 0 0 2px;
}

.drop-menu .fa-times {
padding: 4px 0 0 6px;
}

.caret-down-selector {
padding-left: 7px;
padding-top: 5px;
}

.results-container {
max-width: 100%;
width: 100%;
border: 1px solid lightgrey;
background-color: white;
border-radius: 3px;
position: absolute;
max-height: 300px;
overflow-y: auto;
z-index: 99999;
top: 36px;
}

.results-container p {
padding: 5px 10px;
margin: 0;
}

.results-container p.result-item:hover {
cursor: pointer;
background-color: #eee;
}

.selected-item {
position: absolute;
top: 7px;
left: 12px;
max-width: calc(100% - 47px);
width: 100%;
}

.selected-item p {
white-space: nowrap;
overflow: hidden;
text-overflow: ellipsis;
margin: 0;
}

.selected-items {
position: absolute;
top: 5px;
left: 27px;
border: solid 1px #cccccc;
border-radius: 3px;
background-color: #fafafa;
padding: 1px 5px;
max-width: calc(100% - 47px);
}

.selected-items p {
white-space: nowrap;
overflow: hidden;
text-overflow: ellipsis;
margin: 0;
margin-right: 15px;
}

.selected-items>i.fa-times {
position: absolute;
top: 4px;
right: 4px;
}

.selected-items>i.fa-times:hover {
cursor: pointer;
}

.conditional-message {
font-size: 0.85em;
margin-top: 5px;
color: #999999;
}

.alert-danger {
padding: 2px 10px;
margin-top: 5px;
margin-bottom: 0px;
}

